#if !UNITY_5_OR_NEWER
public static class EnteringPoint
{
    private const string VERSION = "1.8.0";
    private const string VERSION_NAME = "Hojicha Edition";

    [STAThread]
    public static int Main(string[] args)
    {
        string? command = null;
        ReadName();

        Init();

        string? name = command;

        while (command != "exit")
        {
            Read();

            if (command is null)
                continue;

            string[] parts = command.Split(' ');

            if (parts[0] == "send")
            {
                NetworkingInput.Send(command.Substring(5));
            }

            else if (parts.Length == 1 && parts[0] == "help")
            {
                Debug.NonAuthLog("<DGE> List of all registered commands:" +
                "\n=================================" +
                "\n<GRE> >> [init] with argument [s/c]" +
                "\n    <DGE>Example: init s" +
                "\n    <DGE>Info: initialises data and components, then starts from Server(s) or Client(c)\n" +
                "\n<GRE> >> [switch] with argument [s/c]" +
                "\n    <DGE>Example: switch c" +
                "\n    <DGE>Info: switches current networking state between Server(s) and Client(c)\n" +
                "\n<GRE> >> [set pairing] with argument [true/false]" +
                "\n    <DGE>Example: set pairing true" +
                "\n    <DGE>Info: starts pairing, if (s): broadcasting credentials, (c): searching for unique servers\n" +
                "\n<GRE> >> [set tickrate] with int argument [?]" +
                "\n    <DGE>Example: set tickrate 16" +
                "\n    <DGE>Info: sets server ticks per second\n" +
                "\n<GRE> >> [show servers] with no arguments" +
                "\n    <DGE>Example: show servers" +
                "\n    <DGE>Info: shows available for conection servers\n" +
                "\n<GRE> >> [draw] with string argument[?]" +
                "\n    <DGE>Example: draw rem&ram" +
                "\n    <DGE>Info: draws text image associated with argument\n" +
                "\n<GRE> >> [exit] with no argument" +
                "\n    <DGE>Example: exit" +
                "\n    <DGE>Info: turns off DILDO and exits application\n" +
                "");
            }

            else if (parts.Length == 1 && parts[0] == "disconnect")
            {
                NetworkingInput.Disconnect();
            }

            else if (parts.Length == 1 && parts[0] == "clear")
            {
                Console.Clear();
                Init();
            }

            else if (parts.Length == 2 && parts[0] == "init")
            {
                NetworkingInput.Init(new UserData()
                {
                    UserName = name,
                    State = parts[1] == "s" ? NetworkingState.SERVER : NetworkingState.CLIENT
                });
                NetworkingOutput.OnPacketReceivedAsync += ProcessPackets;
            }

            else if (parts.Length == 2 && parts[0] == "switch")
            {
                NetworkingInput.Switch(parts[1] == "s" ? NetworkingState.SERVER : NetworkingState.CLIENT);
            }

            else if (parts.Length == 2 && parts[0] == "draw")
            {
                (parts[1] switch
