                    _ => null
                })?.Invoke();
            }

            else if (parts.Length == 2 && parts[0] == "show" && parts[1] == "servers")
            {
                (Guid guid, string name)[]? servers = NetworkingInput.GetAvailableServers();
                if (servers is not null)
                {
                    int i = 0;
                    foreach (var server in servers)
                        Debug.Log<ClientState>($"<WHI> [{i++}]\t<GRA>Name: <CYA>{server.name}<GRA> \n\t\t\t\t\t\t\tGUID: <CYA>{server.guid}");
                }
            }

            else if (parts.Length == 2 && parts[0] == "connect")
            {
                if (int.TryParse(parts[1], out int result))
                    NetworkingInput.Connect(result);
            }

            else if (parts.Length == 3 && parts[0] == "set" && parts[1] == "pairing")
            {
                NetworkingInput.SetPairing(parts[2] == "true");
            }

            else if (parts.Length == 3 && parts[0] == "set" && parts[1] == "tickrate")
            {
                if (int.TryParse(parts[2], out int result))
                    NetworkingInput.ConfigurateServer(result);
            }
        }

        return 0;

        void Init()
        {
            DrawDILDOStart();

            Debug.NonAuthLog("<DGE>Enter \"init s\" or \"init c\" in order to setup data and start from Server(s) or Client(s)." +
                             "\nType  \"help\" to discover all commands" +
                             "\n__________________________________________________________________________________________________________");
        }
        void Read()
        {
            Thread.Sleep(300);
            Console.ForegroundColor = ConsoleColor.White;
            Console.Write(">> ");
            command = Console.ReadLine();
        }
        void ReadName()
        {
            Console.Write('\n');
            while (command is null || command.Length == 0)
            {
                Console.ForegroundColor = ConsoleColor.White;
                Console.Write(">> Enter name: ");
                command = Console.ReadLine();
            }
            Console.Write('\n');
        }
    }

    private static void ProcessPackets()
    {
        var packets = NetworkingOutput.DequeueAllPackets();
        foreach (var packet in packets)
        {
            if(packet.TypeName != typeof(string).Name)
                continue;

            Debug.Log($"<DYE>{(string)packet.ObjectData}");
        }
    }
